package entities;import javax.persistence.*;import java.util.ArrayList;import java.util.List;@Table(name = "movie")@Entity@NamedQueries({        @NamedQuery(name = "Movie.deleteAllRows", query = "DELETE from Movie"),        @NamedQuery(name = "Movie.getAll", query = "Select m from Movie m"),        @NamedQuery(name = "Movie.getByTitle", query = "Select m from Movie m where m.titel like :title")})public class Movie {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id", nullable = false)    private Integer id;    private String titel;    private int year;    @ElementCollection    @CollectionTable(name="actor", joinColumns = @JoinColumn(name="id", referencedColumnName = "id"))    @Column(name = "actor")    List<String> actors = new ArrayList<String>();        public Movie() {    }    public Movie(String titel, int year, List<String> actors) {        this.titel = titel;        this.year = year;        this.actors = actors;    }    public Integer getId() {        return id;    }    public void setId(Integer id) {        this.id = id;    }    public String getTitel() {        return titel;    }    public void setTitel(String titel) {        this.titel = titel;    }    public int getYear() {        return year;    }    public void setYear(int year) {        this.year = year;    }    public List<String> getActors() {        return actors;    }    public void setActors(List<String> actors) {        this.actors = actors;    }}